func longByteToIpv4(longIp int64) string {
	return fmt.Sprintf("%d.%d.%d.%d", longIp&0xFF, (longIp>>8)&0xFF, (longIp>>16)&0xFF, (longIp>>24)&0xFF)
}

func ipv6ToStrByte(ipv6 string) string {
	compressIndex := strings.Index(ipv6, "::")
	if compressIndex != -1 {
		gapNum := 9 - len(strings.Split(ipv6, ":"))
		ipv6 = strings.Replace(ipv6, "::", ":"+strings.Repeat("0000:", gapNum), 1)
	}
	str := strings.Split(ipv6, ":")
	if len(str) != 8 {
		panic("Non-standard IPv6 address")
	}
	var builder strings.Builder
	for i := 0; i < len(str)-1; i += 2 {
		ipBreak := str[i] + str[i+1]
		builder.WriteString(fmt.Sprintf("%d", ipBreak))
		if i != len(str)-2 {
			builder.WriteString("-")
		}
	}
	return builder.String()
}

func stringByteToIpv6(strByte string, streamline ...bool) string {
	strBytes := strings.Split(strByte, "-")
	var str strings.Builder
	for _, byteStr := range strBytes {
		allIp := fmt.Sprintf("%08x", byteStr)
		str.WriteString(allIp)
	}
	result := str.String()
	if len(streamline) > 0 && streamline[0] {
		result = strings.Replace(result, "0000:0000:", "::", 1)
	}
	return result
