import os
import xml.etree.ElementTree as ET

def extract_weapon_names(base_folder, output_file):
    with open(output_file, 'w') as outfile:
        for root, dirs, files in os.walk(base_folder):
            for file in files:
                if file.endswith('weapons.meta'):
                    file_path = os.path.join(root, file)
                    try:
                        tree = ET.parse(file_path)
                        root_element = tree.getroot()
                        for weapon in root_element.findall('.//Name'):
                            if weapon.text.startswith('WEAPON_'):
                                outfile.write(weapon.text + '\n')
                    except ET.ParseError:
                        print(f"Error parsing {file_path}")

# Example usage
extract_weapon_names('/path/to/base/folder', 'weapons_list.txt')

