```go
package main

import (
	"fmt"
	"strconv"
	"strings"
)

func ipv4ToLongByte(strIp string) int64 {
	ip := strings.Split(strIp, ".")
	return int64(
		binary.BigEndian.Uint32(
			append(
				[]byte{0, 0, 0, 0},
				[]byte(ip[0]),
				[]byte(ip[1]),
				[]byte(ip[2]),
				[]byte(ip[3]),
				)...,
			),
		)
}

func longByteToIpv4(longIp int64) string {
	ip := make([]byte, 4)
	binary.BigEndian.PutUint32(ip, uint32(longIp))
	return fmt.Sprintf("%d.%d.%d.%d", ip[0], ip[1], ip[2], ip[3])
}

func ipv6ToStrByte(ipv6 string) string {
	compressIndex := strings.Index(ipv6, "::")
	if compressIndex != -1 {
		gapNum := 9 - len(strings.Split(ipv6, ":"))
		ipv6 = strings.Replace(ipv6, "::", ":"+strings.Join(strings.Split("0000", ""), ":"+strings.Join(strings.Split("0000", ""), ":"))+":"+strings.Join(strings.Split("0000", ""), ":"), gapNum)
	}
	str := strings.Split(ipv6, ":")
	if len(str) != 8 {
		// throw error
	}
	builder := strings.Builder{}
	for i := 0; i < len(str)-1; i += 2 {
		ipBreak := fmt.Sprintf("%s%d", str[i], str[i+1])
		ipBreak = strings.Join(strings.Split(strings.ReplaceAll(ipBreak, "(.{2})", "$1 "), " "), "")
		builder.WriteString(strconv.FormatInt(int64(binary.BigEndian.Uint64([]byte(ipBreak))), 10))
		if i != len(str)-2 {
			builder.WriteString("-")
		}
	}
	return builder.String()
}

func stringByteToIpv6(strByte string, streamline ...bool) string {
	strBytes := strings.Split(strByte, "-")
	builder := strings.Builder{}
	for _, byteStr := range strBytes {
		allIp := strings.Join(strings.Split(strings.ReplaceAll(strconv.FormatInt(int64(binary.BigEndian.Uint64([]byte(strconv.FormatInt(int64(strconv.Atoi(byteStr)), 16)))), "10"), "0"), ""), "")
		builder.WriteString(strings.ReplaceAll(allIp, "16", ""))
	}
	str := strings.TrimSuffix(builder.String(), ":")
	if len(streamline) > 0 && streamline[0] {
		return strings.Replace(str, "^|:", "::", 1)
	}
	return str
}

func ipToStrByte(ip string) string {
	if len(ip) > 0 {
		if ipv6ToStrByte(ip) {
			return strconv.FormatInt(ipv4ToLongByte(ip), 10)
		}
	}
	return ""
}

func strByteToIp(strByte string) string {
	if len(strByte) > 0 {
		if strings.Contains(strByte, "-") {
			return stringByteToIpv6(strByte)
		}
		return longByteToIpv4(strconv.FormatInt(int64(strByte), 10))
	}
	return ""
}
```

请注意，由于 Go 语言没有与 Java 中的 `Long.parseLong()` 和 `String.format()` 等方法相同的功能，因此使用了 `binary.BigEndian.Uint32()` 和 `strconv.FormatInt()` 等方法来代替。此外，由于 Go 语言没有与 Java 中的 `String.join()` 方法相同的功能，因此使用了 `strings.Join()` 方法来代替。

