    public static long ipv4ToLongByte(String strIp) {
        String[] ip = strIp.split("\\.");
        return (Long.parseLong(ip[3]) << 24) + (Long.parseLong(ip[2]) << 16) + (Long.parseLong(ip[1]) << 8) + Long.parseLong(ip[0]);
    }

    public static String longByteToIpv4(long longIp) {
        StringBuffer buffer = new StringBuffer("");
        buffer.append(longIp & 0x000000FF);
        buffer.append(".");
        buffer.append((longIp & 0x0000FFFF) >>> 8);
        buffer.append(".");
        buffer.append((longIp & 0x00FFFFFF) >>> 16);
        buffer.append(".");
        buffer.append(longIp >>> 24);
        return buffer.toString();
    }

    public static String ipv6ToStrByte(String ipv6) {
        int compressIndex = ipv6.indexOf("::");
        StringBuilder builder = new StringBuilder();
        if (compressIndex != -1) {
            int gapNum = 9 - ipv6.split(":").length;
            ipv6 = ipv6.replace("::", ":" + String.join(":", Collections.nCopies(gapNum, "0000")) + ":");
        }
        String[] str = ipv6.split(":");
        if (str.length != 8) {
            throw new ServerException("非标准IPv6地址");
        }
        for (int i = 0; i < str.length - 1; i += 2) {
            String ipBreak = str[i] + str[i + 1];
            ipBreak = String.join("", ArrayUtil.reverse(ipBreak.replaceAll("(.{2})", "$1 ").split(" "))).trim();
            builder.append(Long.parseLong(ipBreak, 16));
            if (i != str.length - 2) {
                builder.append("-");
            }
        }
        return builder.toString();
    }

    public static String stringByteToIpv6(String strByte, boolean... streamline) {
        String[] strBytes = strByte.split("-");
        StringBuilder str = new StringBuilder();
        for (String byteStr : strBytes) {
            String allIp = String.join("", ArrayUtil.reverse(String.format("%08x", Long.parseLong(byteStr)).replaceAll("(.{2})", "$1 ").split(" "))).trim();
            str.append(allIp.replaceAll("(.{4})", "$1:"));
        }
        str = new StringBuilder(str.substring(0, str.length() - 1));
        if (ObjectUtil.isNotEmpty(streamline) && streamline[0]) {
            return str.toString().replaceFirst("(^|:)(0+(:|$)){2,8}", "::");
        }
        return str.toString();
    }

    public static String ipToStrByte(String ip) {
        if (StrUtil.isNotEmpty(ip)) {
            if (ip.contains(":")) {
                return ipv6ToStrByte(ip);
            }
            return ipv4ToLongByte(ip) + "";
        }
        return null;
    }

    public static String strByteToIp(String strByte) {
        if (StrUtil.isNotEmpty(strByte)) {
            if (strByte.contains("-")) {
                return stringByteToIpv6(strByte);
            }
            return longByteToIpv4(Long.parseLong(strByte));
        }
        return null;
    }
