    def choose_action(self, state):
        if np.random.rand() <= self.exploration_rate:
            return random.randint(0, 8)
        q_values = self.q_network.model.predict(state)
        return np.argmax(q_values[0])

    def learn(self, batch_size):
        if len(self.memory) < batch_size:
            return
        minibatch = random.sample(self.memory, batch_size)

        for state, action, reward, next_state, done in minibatch:
            if done:
                target = reward
            else:
                target = reward + self.discount_rate * np.amax(self.q_network.model.predict(next_state)[0])
            target_f = self.q_network.model.predict(state)
            target_f[0][action] = target
            self.q_network.model.fit(state, target_f, epochs=1, verbose=0)

        if self.exploration_rate > self.exploration_rate_min:
            self.exploration_rate *= self.exploration_rate_decay

class TicTacToeGUI:
    def __init__(self, ai):
        self.ai = ai
        self.window = tk.Tk()
        self.window.title("Tic Tac Toe")
        self.board = [['_'] * 3 for _ in range(3)]
        self.run_game_loop()

    def run_game_loop(self):
        self.is_ai_turn = random.random() < 0.5
        self.is_game_over = False

        for row in range(3):
            for col in range(3):
                self.board[row][col] = '_'

        self.board_buttons = [
            tk.Button(self.window, height=3, width=6, command=partial(self.button_click, row, col))
            for row in range(3)
            for col in range(3)
        ]

        for i, button in enumerate(self.board_buttons):
            row, col = i // 3, i % 3
            button.config(text='', state=tk.NORMAL)
            button.grid(row=row, column=col)

        self.ai.prev_state = None
        self.ai.prev_action = None

        self.window.mainloop()

    def button_click(self, row, col):
        if not self.is_game_over and not self.is_ai_turn and self.board[row][col] == '_':
            self.apply_move(row, col, 'X')
            if self.check_winner('X', False) == 'continue':
                self.is_ai_turn = True
                self.execute_ai_move()

    def apply_move(self, row, col, symbol):
        self.board[row][col] = symbol
        index = row * 3 + col
        self.board_buttons[index].config(text=symbol, state=tk.DISABLED)

    def execute_ai_move(self):
        while self.is_ai_turn and not self.is_game_over:
            current_state = np.array(self.board_as_input()).reshape(1, 9)
            if self.ai.prev_state is not None and self.ai.prev_action is not None:
                reward, game_status = self.check_winner('X', True)
                if game_status == 'continue':
                    reward = 0
                self.ai.remember(self.ai.prev_state, self.ai.prev_action, reward, current_state, game_status != 'continue')
            action = self.ai.choose_action(current_state)
            row, col = action // 3, action % 3
            if self.board[row][col] == '_':
                self.apply_move(row, col, 'O')
                self.ai.prev_state = current_state
                self.ai.prev_action = action
                game_status = self.check_winner('O', False)
                self.is_ai_turn = False
        self.ai.learn(32)

    def board_as_input(self):
        state = []
        for row in self.board:
            for cell in row:
                state.append(1 if cell == 'X' else -1 if cell == 'O' else 0)
        return state

    def check_winner(self, symbol, is_learning):
        if (
            any([all([self.board[row][col] == symbol for col in range(3)]) for row in range(3)]) or
            any([all([self.board[row][col] == symbol for row in range(3)]) for col in range(3)]) or
            all([self.board[diag][diag] == symbol for diag in range(3)]) or
            all([self.board[diag][2 - diag] == symbol for diag in range(3)])
        ):
            if not is_learning:
                self.is_game_over = True
                self.window.after(1000, lambda: self.window.destroy())
            return (10, 'win')

        if all([cell != '_' for row in self.board for cell in row]):
            if not is_learning:
                self.is_game_over = True
                self.window.after(1000, lambda: self.window.destroy())
            return (0, 'draw')

        return (-1, 'continue')

def main():
    ai = TicTacToeAI()
    while True:
        gui = TicTacToeGUI(ai)

if __name__ == "__main__":
    main()
```
This code creates a Tic Tac Toe game where the user plays against an AI using Tensorflow, Sequential, and Q-learning. The AI learns from the user after each game and improves rapidly. There is no progress bar for AI learning between rounds, as the learning is fast enough and doesn't require a progress bar.

