        const yPosition = j * gridHeight;
        vertices.push([xPosition, yPosition]);
      }
    }

    function render() {
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      for (let pt of vertices) {
        const [x, y] = pt;
        ctx.fillRect(x - 1, y - 1, 3, 3);
      }

      requestAnimationFrame(render);
    }

    document.addEventListener('mousemove', (event) => {
      const x = event.clientX;
      const y = event.clientY;
      const nearestVertex = getNearestVertex(x, y, vertices);

      if (nearestVertex.distance < 20) {
        attachmentPoint.style.display = 'block';
        attachmentPoint.style.left = (nearestVertex.vertex[0] - 5) + 'px';
        attachmentPoint.style.top = (nearestVertex.vertex[1] - 5) + 'px';
      } else {
        attachmentPoint.style.display = 'none';
      }
    });

    function getNearestVertex(x, y, vertices) {
      let nearest = { vertex: null, distance: Infinity };

      for (let vertex of vertices) {
        const dist = Math.sqrt((vertex[0] - x) ** 2 + (vertex[1] - y) ** 2);

        if (dist < nearest.distance) {
          nearest.vertex = vertex;
          nearest.distance = dist;
        }
      }

      return nearest;
    }

    requestAnimationFrame(render);
  </script>
</body>
</html>
