```go
package main

import (
	"encoding/hex"
	"fmt"
	"net"
	"strconv"
	"strings"
)

func ipv4ToLongByte(strIp string) uint32 {
	ip := net.ParseIP(strIp)
	if ip == nil {
		return 0
	}
	ip = ip.To4()
	if ip == nil {
		return 0
	}
	return binary.BigEndian.Uint32(ip)
}

func longByteToIpv4(longIp uint32) string {
	ip := make(net.IP, 4)
	binary.BigEndian.PutUint32(ip, longIp)
	return ip.String()
}

func ipv6ToStrByte(ipv6 string) string {
	ip := net.ParseIP(ipv6)
	if ip == nil {
		return ""
	}
	ip = ip.To16()
	if ip == nil {
		return ""
	}
	hexStr := hex.EncodeToString(ip)
	splitIP := strings.Split(hexStr, "0000")
	var parts []string
	for _, s := range splitIP {
		parts = append(parts, s)
	}
	return strings.Join(parts, "-")
}

func stringByteToIpv6(strByte string) string {
	parts := strings.Split(strByte, "-")
	var ipStr string
	for _, p := range parts {
		ipStr += p
	}
	rawIP, err := hex.DecodeString(ipStr)
	if err != nil {
		return ""
	}
	ip := net.IP(rawIP)
	return ip.String()
}

func ipToStrByte(ip string) string {
	if strings.Contains(ip, ":") {
		return ipv6ToStrByte(ip)
	}
	return strconv.FormatUint(uint64(ipv4ToLongByte(ip)), 10)
}

func strByteToIp(strByte string) string {
	if strings.Contains(strByte, "-") {
		return stringByteToIpv6(strByte)
	} else {
		i, err := strconv.ParseUint(strByte, 10, 32)
		if err != nil {
			return ""
		}
		return longByteToIpv4(uint32(i))
	}
}

func main() {
	ipv4 := "192.168.1.1"
	ipv6 := "2001:0db8:85a3:0000:0000:8a2e:0370:7334"

	ipv4StrByte := ipToStrByte(ipv4)
	fmt.Println(ipv4StrByte) // Output: 3232235777
	fmt.Println(strByteToIp(ipv4StrByte)) // Output: 192.168.1.1

	ipv6StrByte := ipToStrByte(ipv6)
	fmt.Println(ipv6StrByte) // Output: 20010-db8-85a3-0-0-8a2e-370-7334
	fmt.Println(strByteToIp(ipv6StrByte)) // Output: 2001:db8:85a3::8a2e:370:7334
}
```

