package com.psl.gems.game;

import com.psl.gems.cards.Card;
import com.psl.gems.cards.PackOfCards;
import com.psl.gems.players.Player;
import java.util.ArrayList;
import java.util.List;

public class Game {
    private final PackOfCards fullPack;
    private final List<Player> players;

    public Game() {
        fullPack = new PackOfCards().initializeFullPack().shuffle();
        players = new ArrayList<Player>();
    }

    public boolean register(Player player) {
        if (players.size() >= 2) {
            return false;
        }
        players.add(player);
        return true;
    }

    public Player play() {
        if (players.size() != 2) {
            return null;
        }

        for (int i = 0; i < 3; i++) {
            for (Player player : players) {
                player.addCard(fullPack.getTopCard());
            }
        }

        Player player1 = players.get(0);
        Player player2 = players.get(1);
        Card highestCard1 = player1.getHighestCard();
        Card highestCard2 = player2.getHighestCard();

        if (highestCard1.compareTo(highestCard2) > 0) {
            return player1;
        } else {
            return player2;
        }
    }
}
