export default class GameElement{

    static ARC_WIDTH = 96; 
    constructor(x,y,deltaX =0,deltaY = 0,srcImage){
        this.x = x;
        this.y = y;
        this.deltaX = deltaX;
        this.deltaY = deltaY;
        this.image = this.#createImage(srcImage);
    }

    #createImage(imageSource) {
        const newImg = new Image();
        newImg.src = imageSource;
        return newImg;
    }
    get width() {
      return this.image.width;
    }
    get height() {
      return this.image.height;
    }

    draw(context){
        context.drawImage(this.image,this.x,this.y);
    }

    move(canvas){
        if((this.x + this.deltaX + GameElement.ARC_WIDTH)> canvas.width){
            this.deltaX = -this.deltaX;
        }
        if((this.x + this.deltaX ) < 0){
            this.deltaX = -this.deltaX;
        }
        if((this.y + this.deltaY + GameElement.ARC_WIDTH)> canvas.height){
            this.deltaY = -this.deltaY;
        }
        if((this.y + this.deltaY ) < 0){
            this.deltaY = -this.deltaY;
        }
        this.x = this.x + this.deltaX;
        this.y = this.y +this.deltaY;
    }
}
import Arc from "./arc";
import Target from "./target";
import KeyManager from './keyManager';
import Arrow from "./arrow";

const alea  = width => Math.floor(Math.random() * width);
export default class Game {

   #canvas;
   // à compléter

   constructor(canvas) {
      this.#canvas = canvas;
      this.context = this.#canvas.getContext("2d");
      this.animation = null
      this.arc = new Arc(this.#canvas.width-300,this.#canvas.height/2 +200);
      this.target = new Target(alea(this.#canvas.width - Target.TARGET_WIDTH));
      this.keyManager = new KeyManager();
      this.arrow = null;
      this.hasShoot = false;
      this.nbArrow = 5;
      this.afficheNbArrow = document.getElementById("nbArrows");
   }

   /** donne accès au canvas correspondant à la zone de jeu */
   get canvas() {
      return this.#canvas;
   }



   animate(){
      this.context.clearRect(0,0,this.#canvas.width,this.#canvas.height);
      this.arc.handleMoveKeys(this.keyManager);
      this.arc.move(this.canvas);
      this.arc.draw(this.context);
      this.target.draw(this.context);
      if(this.hasShoot){
         this.arrow.move();
         this.arrow.draw(this.context);
      }if(this.nbArrow<0){
         this.hasShoot=false;
      }
      this.afficheNbArrow.textContent = this.nbArrow;
      this.animation = window.requestAnimationFrame(this.animate.bind(this));
   }

   startAndStop(){
      if(this.animation == null){
         this.animation = window.requestAnimationFrame(this.animate.bind(this));
      }
      else{
         this.animation = window.cancelAnimationFrame(this.animate.bind(this));
         this.animation = null;
      }
   }


   keyDownActionHandler(event) {
      switch (event.key) {
          case "ArrowLeft":
          case "Left":
              this.keyManager.leftPressed();
              break;
          case "ArrowRight":
          case "Right":
              this.keyManager.rightPressed();
              break;
          case "ArrowUp":
          case "Up":
              this.keyManager.upPressed();
              break;
          case "ArrowDown":
          case "Down":
              this.keyManager.downPressed();
              break;
          case " ":
          case "Space":
             // this.hasShoot = true;
             // this.arrow = new Arrow(this.arc.x - this.arc.width/2,this.arc.y);
             this.shoot();
              this.keyManager.spacePressed();
              break
          default: return;
      }
      event.preventDefault();
   }


   keyUpActionHandler(event) {
      switch (event.key) {
         case "ArrowLeft":
         case "Left":
            this.keyManager.leftReleased();
            break;
         case "ArrowRight":
         case "Right":
            this.keyManager.rightReleased();
            break;
