```javascript
// quiver.js
import GameElement from "./gameElement"

export default class Quiver extends GameElement {
  static QUIVER_IMAGE_SRC = 'src/scripts/assets/images/fleches.png'

  constructor(x, y, deltaY = 5) {
    super(x, y, 0, deltaY, Quiver.QUIVER_IMAGE_SRC)
  }

  static randomYPosition() {
    return 100 + Math.floor(Math.random() * 300)
  }

  checkCollisionWithArc(arc) {
    return (
      this.x < arc.x + arc.width &&
      this.x + this.width > arc.x &&
      this.y < arc.y + arc.height &&
      this.y + this.height > arc.y
    )
  }

  checkCollisionWithArrow(arrow) {
    return (
      this.x < arrow.x + arrow.width &&
      this.x + this.width > arrow.x &&
      this.y < arrow.y + arrow.height &&
      this.y + this.height > arrow.y
    )
  }
}
```

