    ip := net.ParseIP(strIp)
    if ip == nil {
        return 0
    }
    return binary.BigEndian.Uint32(ip.To4())
}

// Convert long integer back to IPv4 string
func longByteToIpv4(longIp uint32) string {
    return fmt.Sprintf("%d.%d.%d.%d", (longIp>>24)&0xFF, (longIp>>16)&0xFF, (longIp>>8)&0xFF, longIp&0xFF)
}

// Convert IPv6 string to a byte representation
func ipv6ToStrByte(ipv6 string) (string, error) {
    ip := net.ParseIP(ipv6)
    if ip == nil {
        return "", fmt.Errorf("非标准IPv6地址")
    }
    var builder strings.Builder
    for i := 0; i < 8; i++ {
        if i > 0 {
            builder.WriteString("-")
        }
        builder.WriteString(fmt.Sprintf("%x", ip[i]))
    }
    return builder.String(), nil
}

// Convert byte representation back to IPv6 string
func stringByteToIpv6(strByte string) string {
    parts := strings.Split(strByte, "-")
    var builder strings.Builder
    for _, byteStr := range parts {
        builder.WriteString(fmt.Sprintf("%04x:", byteStr))
    }
    return strings.TrimSuffix(builder.String(), ":")
}

// Main function to determine IP type and convert accordingly
func ipToStrByte(ip string) (string, error) {
    if strings.Contains(ip, ":") {
        return ipv6ToStrByte(ip)
    }
    return fmt.Sprintf("%d", ipv4ToLongByte(ip)), nil
}

// Convert string byte back to IP
func strByteToIp(strByte string) (string, error) {
    if strings.Contains(strByte, "-") {
        return stringByteToIpv6(strByte), nil
    }
    longIp, _ := strconv.ParseUint(strByte, 10, 32)
    return longByteToIpv4(uint32(longIp)), nil
